		axios
			.get('https://localhost:44344/api/MinMax/userReviewsMinMax')
			.then(response => {
				console.log(response)
				this.setState({ posts: response.data })
			})
			.catch(error => {
        console.log(error)
        this.setState({errorMsg: 'Error retrieving data'})
			})
	}

	
	MinAdd(post){
		this.setState({Min:post.answer})
		this.setState({Minid:post.id})
		console.log(post.id,'id')
	}

	MaxAdd(post){
		this.setState({Max:post.answer})
		this.setState({Maxid:post.id})
		console.log(post.id,'id')
	}
	render() {
		const { posts, errorMsg,Min } = this.state
		
		const minmax =posts.map(post=>(
			console.log(this.state.Min,'this state'),
			console.log(post.answer, 'postanswer'),
			
			this.state.Min > post.answer ? this.MinAdd(post):this.state.Min,
			this.state.Max < post.answer ? this.MaxAdd(post):this.state.Max
		))
		
			
		return (
			<div>
                   <h1> min is: {this.state.Min} with id {this.state.Minid} </h1> 
				   <h1> max is: {this.state.Max} with id {this.state.Maxid} </h1> 
        {errorMsg ? <div>{errorMsg}</div> : null}
			</div>
		)
	}
}

export default MinMax
