import React, { Component,useState,useEffect } from 'react'
import axios from 'axios'
import CommentList from './CommentList'
import Posts from './Pagination/Posts'
import { Pagination } from './Pagination/Pagination'

class Comments extends Component {
	constructor(props) {
		super(props)

		this.state = {
      posts: [],
      errorMsg: '',
	  currentpage:'1',
	  postsPerPage:'10'
		}
	}


   

	componentDidMount() {
		axios
			.get('https://localhost:44344/api/Basic/comments')
			.then(response => {
				console.log(response)
				this.setState({ posts: response.data })
			})
			.catch(error => {
        console.log(error)
        this.setState({errorMsg: 'Error retrieving data'})
			})
	}


	setCurrentPage(pageNumber){
		this.setState({
			currentpage:{pageNumber}
		})

	}

	render() {
		const { posts, errorMsg } = this.state
		// let currentpage=1;
		// let postsPerPage=10;

	// 	const Pages=()=>{
	// 		const[posts,setPosts]=useState([]);
	// //	const[loading,setLoading]=useState(false);
	// 	const[currentpage,setCurrentPage]=useState(1);
	// 	const [postsPerPage,setPostsPerPage]=useState(10);

		

	// 	}

		
		// const commentlist=posts.map(post => (
		// 	<CommentList key={post.id} post={post}></CommentList >

		// ))
	

		const indexOfLastPost=this.state.currentpage*this.state.postsPerPage;
		const indexOfFirstPost=indexOfLastPost-this.state.postsPerPage;
		const currentPosts=posts.slice(indexOfFirstPost,indexOfLastPost);

		const paginate=(pageNumber)=>this.setCurrentPage(pageNumber);


		console.log({currentPosts},' as current posts. Index of last: ', {indexOfLastPost})
		return (
			<div className='container mt-5'>
				   ID   ,  Reviwer, Assingment
				   <Posts posts={currentPosts}/>
				   <Pagination postsPerPage={this.state.postsPerPage} totalPosts={this.state.posts.length} paginate={paginate}/>
               {/* {commentlist} */}

				
        {errorMsg ? <div>{errorMsg}</div> : null}
			</div>
		)
	}
}

export default Comments
