      posts: [],
      errorMsg: '',
	  Minid:0,
	  Maxid:0,
	  Min:100,
	  Max:-1
		}
	}


    clickHandler(){
        
        
    }

	componentDidMount() {
		axios
			.get('https://localhost:44344/api/MinMax/userReviewsMinMax')
			.then(response => {
				console.log(response)
				this.setState({ posts: response.data })
			})
			.catch(error => {
        console.log(error)
        this.setState({errorMsg: 'Error retrieving data'})
			})
	}


	checkMinMax(posts){
		var Min=100;
		var Max=-1;
		var Maxid,Minid;
	
		
		posts.map((post)=>
	
		 post.answer>Max ? {Max: post.answer,Maxid:post.id} : post
		 //post.answer<Min ? {Min:post.answer,Minid:post.id} : post
		)
		return (
			<h1>	Max:{Max}</h1>
		)
	}
	

	render() {
		const { posts, errorMsg } = this.state
		
		
		// const minmaxlist=posts.map(post => (
		// 	console.log(this.Min),
