		// 	post.answer<this.Min ? {Min:post.answer,Minid:post.id} : post

		// ))

			
		return (
			<div>
                    <h1> 
                        </h1> 
					<this.checkMinMax/>
              {/* // {minmaxlist} */}
			  {/* <MinMaxList  {...posts}></MinMaxList > */}
        {errorMsg ? <div>{errorMsg}</div> : null}
			</div>
		)
	}
}

export default MinMax
