import React, { Component } from 'react'
import axios from 'axios'
class PostListAgain extends Component {
	constructor(props) {
		super(props)

		this.state = {
      posts: [],
      errorMsg: ''
		}
	}


    clickHandler(){
        
        
    }

	componentDidMount() {
		axios
			.get('https://localhost:44344/api/sample/user')
			.then(response => {
				console.log(response)
				this.setState({ posts: response.data })
			})
			.catch(error => {
        console.log(error)
        this.setState({errorMsg: 'Error retrieving data'})
			})
	}

	render() {
		const { posts, errorMsg } = this.state
		return (
			<div>
                 <button onClick={this.clickHandler}>Show  me</button>
				List of posts
				{posts.length
					? posts.map(post => <div key={post.id}>Id:{post.id} , Title: {post.userRole}</div>)
          : <div>No data </div>}
        {errorMsg ? <div>{errorMsg}</div> : null}
			</div>
		)
	}
}

export default PostListAgain
